// Filter Location Data - validates data and determines if it should be processed
const item = $input.first();

try {
  // Get the data, handling the nested structure
  let inputData = item;

  // Handle different possible structures
  if (item.json && item.json.body && item.json.body.lat) {
    // Data is in item.json.body
    inputData = item.json.body;
  } else if (item.body && item.body.lat) {
    // Data is in item.body
    inputData = item.body;
  }

  // Basic validation - does it have coordinates?
  const hasValidCoordinates = 
    inputData.lat !== undefined && 
    inputData.lon !== undefined;

  console.log("Data structure:", JSON.stringify(item));
  console.log("Using data:", JSON.stringify(inputData));
  console.log("Has coordinates:", hasValidCoordinates);

  if (hasValidCoordinates) {
    // Create a properly structured output
    return [{
      json: {
        source: 'owntracks',
        type: inputData._type || 'location',
        coordinates: {
          latitude: inputData.lat,
          longitude: inputData.lon,
          accuracy: inputData.acc || 0
        },
        device: {
          battery: inputData.batt || 0,
          trigger: inputData.t || '',
          timestamp: inputData.tst || 0
        },
        raw: inputData,
        valid: true,
        skip: false
      }
    }];
  } else {
    // Log invalid data but still return something
    console.log('Invalid data received:', JSON.stringify(inputData));
    return [{
      json: {
        valid: false,
        skip: true,
        raw: item,
        reason: 'Missing coordinates'
      }
    }];
  }
} catch (error) {
  // Handle any errors
  console.error('Error processing Owntracks data:', error);
  return [{
    json: {
      valid: false,
      skip: true,
      error: error.message,
      raw: item
    }
  }];
}

// Format for Poke Webhook
const item = $input.first();
try {
  // Extract the data we need
  const locationData = {
    source: item.json.source,
    type: item.json.type,
    coordinates: item.json.coordinates,
    device: item.json.device
  };

  // Convert to the format Poke expects
  return [{
    json: {
      message: JSON.stringify(locationData)
    }
  }];
} catch (error) {
  console.error('Error formatting for Poke:', error);
  return [{
    json: {
      message: JSON.stringify({
        error: error.message,
        source: "owntracks"
      })
    }
  }];
}

// Success handler
const item = $input.item.json;

console.log('✅ Successfully sent to Poke');
console.log('Response:', JSON.stringify(item, null, 2));

return {
  json: {
    success: true,
    status: item.statusCode || 200,
    message: 'Location update sent to Poke',
    timestamp: new Date().toISOString()
  }
};

// Skipped handler
const item = $input.item.json;

console.log('⏭️  Location update skipped');
console.log('Reason:', item.reason || 'No reason provided');

return {
  json: {
    skipped: true,
    reason: item.reason,
    timestamp: new Date().toISOString()
  }
};

// Error handler
const error = $input.item.json;

console.error('❌ Error processing location update');
console.error('Error:', JSON.stringify(error, null, 2));

// Return error details
return {
  json: {
    error: true,
    message: error.message || 'Unknown error',
    details: error,
    timestamp: new Date().toISOString()
  }
};

