// Owntracks Location Data Filter and Transformer
// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”

const item = $input.item.json;

const MIN_ACCURACY = 50;  // meters
const MIN_DISTANCE = 20;  // meters (reserved for future use)

// Log incoming data for debugging
console.log('ğŸ“ Received Owntracks data:', JSON.stringify(item, null, 2));

// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
// EXTRACT OWNTRACKS DATA
// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”

// Owntracks sends data in this format:
// {
//   "_type": "location",
//   "lat": 51.5074,
//   "lon": -0.1278,
//   "acc": 10,
//   "tst": 1234567890,
//   "alt": 100,
//   "vel": 0,
//   "batt": 85,
//   "tid": "JF",
//   "t": "u"
// }

const data = {
  type: item._type || 'location',
  latitude: item.lat,
  longitude: item.lon,
  accuracy: item.acc,
  timestamp: item.tst,
  altitude: item.alt,
  velocity: item.vel,
  battery: item.batt,
  tracker_id: item.tid,
  trigger: item.t // 'u' = user, 'p' = ping, 't' = timer, etc.
};

// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
// VALIDATION
// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”

const validationErrors = [];

if (data.latitude === undefined || data.longitude === undefined) {
  validationErrors.push('Missing latitude or longitude');
}

if (data.latitude < -90 || data.latitude > 90) {
  validationErrors.push('Invalid latitude: ' + data.latitude);
}

if (data.longitude < -180 || data.longitude > 180) {
  validationErrors.push('Invalid longitude: ' + data.longitude);
}

if (validationErrors.length > 0) {
  console.error('âŒ Validation errors:', validationErrors);

  return {
    json: {
      error: 'Validation failed',
      details: validationErrors,
      skip: true
    }
  };
}

// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
// FILTERING LOGIC
// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”

const shouldSkip = [];

// Filter 1: GPS Accuracy
// Skip if accuracy is worse than threshold (higher = worse)
if (data.accuracy && data.accuracy > MIN_ACCURACY) {
  shouldSkip.push(`Low accuracy: ${data.accuracy}m > ${MIN_ACCURACY}m`);
}

// Filter 2: Significant movement
// (Future enhancement: compare with last known stored position)

// Filter 3: Ignore certain trigger types if needed
// Example: ['p'] to ignore pings
const ignoredTriggers = [];

if (data.trigger && ignoredTriggers.includes(data.trigger)) {
  shouldSkip.push(`Ignored trigger type: ${data.trigger}`);
}

if (shouldSkip.length > 0) {
  console.log('â­ï¸ Skipping location update:', shouldSkip);

  return {
    json: {
      skip: true,
      reason: shouldSkip,
      data: data
    }
  };
}

// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”
// TRANSFORM FOR POKE
// â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”

// Convert timestamp to ISO format
const timestamp = data.timestamp
  ? new Date(data.timestamp * 1000).toISOString()
  : new Date().toISOString();

// Format data for Poke API
const pokePayload = {
  type: 'location_update',
  source: 'owntracks',
  timestamp,
  location: {
    latitude: data.latitude,
    longitude: data.longitude,
    accuracy: data.accuracy,
    altitude: data.altitude,
    velocity: data.velocity
  },
  device: {
    tracker_id: data.tracker_id,
    battery: data.battery,
    trigger: data.trigger
  },
  metadata: {
    raw_timestamp: data.timestamp,
    processed_at: new Date().toISOString()
  }
};

console.log('âœ… Location update passed filters');
console.log('ğŸ“¤ Sending to Poke:', JSON.stringify(pokePayload, null, 2));

return {
  json: {
    skip: false,
    poke_payload: pokePayload,
    original_data: data
  }
};

// Success handler
const item = $input.item.json;

console.log('âœ… Successfully sent to Poke');
console.log('Response:', JSON.stringify(item, null, 2));

return {
  json: {
    success: true,
    status: item.statusCode || 200,
    message: 'Location update sent to Poke',
    timestamp: new Date().toISOString()
  }
};

// Skipped handler
const item = $input.item.json;

console.log('â­ï¸  Location update skipped');
console.log('Reason:', item.reason || 'No reason provided');

return {
  json: {
    skipped: true,
    reason: item.reason,
    timestamp: new Date().toISOString()
  }
};

// Error handler
const error = $input.item.json;

console.error('âŒ Error processing location update');
console.error('Error:', JSON.stringify(error, null, 2));

// Return error details
return {
  json: {
    error: true,
    message: error.message || 'Unknown error',
    details: error,
    timestamp: new Date().toISOString()
  }
};

